class Image
  attr_reader :pixels

  def initialize(pixels)
    @pixels = pixels
  end

  def output_image
    @pixels.each do |row|
      puts row.join
    end
  end

  def blur(n = 1)
    arr = Marshal.load(Marshal.dump(@pixels))
    return Image.new(arr) if n.zero?
    y = arr.size
    x = arr.max_by{|row| row.size}.size
    for j in (0...y)
      for i in (0...x)
        if @pixels[j][i] == 1
          arr[j-1][i] = 1 unless j-1 < 0
          arr[j+1][i] = 1 unless j+1 >= y
          arr[j][i-1] = 1 unless i-1 < 0
          arr[j][i+1] = 1 unless i+1 >= x
        end
      end
    end
    arr_img = Image.new(arr)
    arr_img.blur(n - 1)
  end
end

